PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE biomarker_dictionary (
	id INTEGER NOT NULL, 
	standard_name VARCHAR, 
	alternate_names TEXT, 
	standard_unit VARCHAR, 
	unit_conversions TEXT, 
	category VARCHAR, 
	reference_ranges TEXT, 
	description TEXT, 
	created_date DATETIME, 
	updated_date DATETIME, 
	PRIMARY KEY (id)
);
CREATE TABLE profiles (
	id CHAR(32) NOT NULL, 
	name VARCHAR NOT NULL, 
	date_of_birth DATETIME, 
	gender VARCHAR, 
	patient_id VARCHAR, 
	created_at DATETIME, 
	last_modified DATETIME, favorite_biomarkers JSON DEFAULT '[]', health_summary TEXT, summary_last_updated DATETIME, user_id VARCHAR(36), 
	PRIMARY KEY (id)
);
CREATE TABLE pdfs (
	id INTEGER NOT NULL, 
	file_id VARCHAR, 
	filename VARCHAR, 
	file_path VARCHAR, 
	upload_date DATETIME, 
	processed_date DATETIME, 
	report_date DATETIME, 
	extracted_text TEXT, 
	status VARCHAR, 
	error_message TEXT, 
	profile_id CHAR(32), 
	lab_name VARCHAR, 
	patient_name VARCHAR, 
	patient_id VARCHAR, 
	patient_age INTEGER, 
	patient_gender VARCHAR, 
	processing_details JSON, 
	parsing_confidence FLOAT, 
	PRIMARY KEY (id), 
	FOREIGN KEY(profile_id) REFERENCES profiles (id)
);
CREATE TABLE biomarkers (
	id INTEGER NOT NULL, 
	pdf_id INTEGER, 
	profile_id CHAR(32), 
	name VARCHAR, 
	original_name VARCHAR, 
	original_value VARCHAR, 
	original_unit VARCHAR, 
	value FLOAT, 
	unit VARCHAR, 
	reference_range_low FLOAT, 
	reference_range_high FLOAT, 
	reference_range_text VARCHAR, 
	category VARCHAR, 
	is_abnormal BOOLEAN, 
	importance INTEGER, 
	extracted_date DATETIME, 
	validated BOOLEAN, 
	validated_by VARCHAR, 
	validated_date DATETIME, 
	notes TEXT, 
	PRIMARY KEY (id), 
	FOREIGN KEY(pdf_id) REFERENCES pdfs (id), 
	FOREIGN KEY(profile_id) REFERENCES profiles (id)
);
CREATE TABLE alembic_version (
	version_num VARCHAR(32) NOT NULL, 
	CONSTRAINT alembic_version_pkc PRIMARY KEY (version_num)
);
DELETE FROM sqlite_sequence;
CREATE UNIQUE INDEX ix_biomarker_dictionary_standard_name ON biomarker_dictionary (standard_name);
CREATE INDEX ix_biomarker_dictionary_id ON biomarker_dictionary (id);
CREATE INDEX ix_profiles_name ON profiles (name);
CREATE INDEX ix_profiles_id ON profiles (id);
CREATE INDEX ix_profiles_patient_id ON profiles (patient_id);
CREATE INDEX ix_pdfs_id ON pdfs (id);
CREATE UNIQUE INDEX ix_pdfs_file_id ON pdfs (file_id);
CREATE INDEX ix_biomarkers_name ON biomarkers (name);
CREATE INDEX ix_biomarkers_id ON biomarkers (id);
CREATE INDEX ix_profiles_user_id ON profiles (user_id);
COMMIT;
